// Alert-es Easter egg
window.addEventListener('keypress', pageKeypress);
var easterKeys = [];
var easterWord = 'alert';

function pageKeypress(event){
	easterKeys.push(event.key);
	while(easterKeys.length > easterWord.length){
		easterKeys = easterKeys.slice(1);
	}
	
	if(easterKeys.join('') == easterWord){
		alertCunami();
	}
}

var alertMessages = [
	'Alert!!!',
	'Nem kaptál olyan infót, hogy felejtsd el az alert-et?',
	'Ez persze kicsi túlzás, ugyanis van ahol így oldják meg a legegyszerűbben a figyelmeztető ablakok megjelenítését.',
	'Így nem lesz ugyanolyan a felugró ablak minden böngészőben.',
	'Egy vicc a végére:',
	'– Apu, az egy selfie bot ott a komódon?\n– Igen, kisfiam.\n– Minek az neked?\n– Tegnap vettem. Ezentúl nem kell lemászni az aknába, hogy leolvassam a vízórát...'
];

function alertCunami(){
	for(var i=0; i < alertMessages.length; ++i){
		alert(alertMessages[i]);
	}
}
